mapper.select:

SELECT board1_no, board1_title, board1_content, board1_regdate, board1_isshow, member_id
FROM board1
WHERE board1_no=#{board1_no}

mapper.update:

UPDATE board1
SET board1_title=#{board1_title}, board1_content=#{board1_content}, board1_regdate=now()
WHERE board1_no=#{board1_no}

mapper.delete:

UPDATE board1
SET board1_isshow = 'N'
WHERE board1_no = #{board1_no};
