mapper.update:

update comment1
set comment1_content = #{comment1_content}, comment1_isshow = #{comment1_isshow}, comment1_regdate = #{comment1_regdate},
member_class = #{member_class}, member_name = #{member_name}
where comment1_no = #{comment1_no} and board1_no = #{board1_no}

mapper.select:

SELECT comment1_no, board1_no, comment1_content, comment1_regdate, comment1_isshow, member_name, member_class
FROM comment1
WHERE comment1_no=#{comment1_no};
