mapper.select:

SELECT board2_no, board2_title, board2_content, board2_regdate, board2_isshow, member_id
FROM board2
WHERE board2_no=#{board2_no}

mapper.update:

UPDATE board2
SET board2_title=#{board2_title}, board2_content=#{board2_content}, board2_regdate=now()
WHERE board2_no=#{board2_no}

mapper.delete:

UPDATE board2
SET board2_isshow = 'N'
WHERE board2_no = #{board2_no};
