mapper.select:

SELECT board3_no, board3_title, board3_content, board3_regdate, board3_isshow, member_id
FROM board3
WHERE board3_no=#{board3_no}

mapper.update:

UPDATE board3
SET board3_title=#{board3_title}, board3_content=#{board3_content}, board3_regdate=now()
WHERE board3_no=#{board3_no}

mapper.delete:

UPDATE board3
SET board3_isshow = 'N'
WHERE board3_no = #{board3_no};
