mapper.select:

SELECT board4_no, board4_title, board4_content, board4_regdate, board4_isshow, member_id
FROM board4
WHERE board4_no=#{board4_no}

mapper.update:

UPDATE board4
SET board4_title=#{board4_title}, board4_content=#{board4_content}, board4_regdate=now()
WHERE board4_no=#{board4_no}

mapper.delete:

UPDATE board4
SET board4_isshow = 'N'
WHERE board4_no = #{board4_no};
