mapper.select:

SELECT *
FROM notice ORDER BY notice_no desc
LIMIT 5;


SELECT *
FROM notice
WHERE notice_no = #{notice_no} AND notice_isshow = 'Y';

mapper.update:
UPDATE notice
SET notice_count = notice_count + 1
WHERE notice_no = #{notice_no};

mapper.select:

SELECT count(*)
FROM notice_comment
WHERE notice_no = #{notice};


SELECT notice_no
FROM notice
WHERE member_id = #{member_id}
ORDER BY notice_regdate DESC
LIMIT 1;

mapper.update:

UPDATE notice
SET notice_isshow = 'N'
WHERE notice_no = #{notice_no};


UPDATE notice_comment
SET comment_isshow = 'N'
WHERE comment_no = #{comment_no};


UPDATE notice
SET notice_title = #{notice_title} AND notice_content = #{notice_content}
WHERE notice_no = #{notice_no};


UPDATE notice_comment
SET comment_content = #{comment_content}
WHERE comment_no = #{comment_no};
